    private void txt_elementryKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_elementryKeyPressed
        // TODO add your handling code here:
          if (evt.getKeyChar() >= '0' && evt.getKeyChar() <= '9' && txt_elementry.getText().length()<8 || evt.getKeyCode() == evt.VK_BACK_SPACE)
 {

txt_elementry.setEditable(true);

}
 else
 {
txt_elementry.setEditable(false);

}

    }//GEN-LAST:event_txt_elementryKeyPressed

    private void txt_afterallKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_afterallKeyPressed
        // TODO add your handling code here:
     if (evt.getKeyChar() >= '0' && evt.getKeyChar() <= '9' && txt_afterall.getText().length()<8|| evt.getKeyCode() == evt.VK_BACK_SPACE)
 {

txt_afterall.setEditable(true);

}
 else
 {
txt_afterall.setEditable(false);

}   

    }//GEN-LAST:event_txt_afterallKeyPressed

