    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
           SimpleDateFormat date=new SimpleDateFormat("YYYY-MM-dd");
           String date1=date.format(date_v.getDate());
       
        String update=db.Update("update voucher_tb1 set  elementary_balence='"+txt_elementry.getText()+"',after_all_balence='"+txt_afterall.getText()+"' where Date='"+date1+"'");
        JOptionPane.showMessageDialog(null, update);

    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        if(date_v.getDate()==null || txt_elementry.getText().isEmpty() || txt_afterall.getText().isEmpty())
         {
             JOptionPane.showMessageDialog(null, "Some fields are required");
             
         }
        else
        {
        SimpleDateFormat date=new SimpleDateFormat("YYYY-MM-dd");
        String date1=date.format(date_v.getDate());
        String insert=db.Insert("insert into voucher_tbl(Date, elementary_balence, after_all_balence)values('"+date1+"','"+txt_elementry.getText()+"','"+txt_afterall.getText()+"')");

        JOptionPane.showMessageDialog(null, insert);
        }

    }//GEN-LAST:event_jButton3ActionPerformed

